import React, { useContext, useEffect, useRef } from "react";
import noteContext from "../context/notes/NoteContext";
import AddNotes from "./AddNotes";
import Noteitem from "./Noteitem";
const Notes = () => {
    const contex = useContext(noteContext);

    const { notes, getAllNotes } = contex;

    useEffect(() => {
        getAllNotes()
    }, [])

    const ref = useRef(null)

    const updateNote = () =>{
        ref.current.click()
    }
    return (
        <>
            <AddNotes />
          
            <button type="button" ref={ref} class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>

      
            <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h1 class="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body">
                        <div class="mb-3">
                    <label for="Title" class="form-label">Title</label>
                    <input type="text" class="form-control" id="title" name="title" aria-describedby="emailHelp" onChange={onChange}/>
                </div>
                <div class="mb-3">
                    <label for="Description" class="form-label">Description</label>
                    <input type="text" class="form-control" id="description" name="description" onChange={onChange}/>
                </div>
                <div class="mb-3">
                    <label for="Tag" class="form-label">Tag</label>
                    <input type="text" class="form-control" id="tag" name="tag" onChange={onChange} />
                </div>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row my-3">
                <h2>Your Notes</h2>
                {
                    notes.map((note) => {
                        // return note.title;
                        return <Noteitem key={note._id} updateNote={updateNote} note={note} />
                    })
                }
            </div>
        </>
    )
}

export default Notes
