import React, { useState } from "react";
import {useNavigate} from "react-router-dom"

const Login = () => {

    const navigate = useNavigate()
    
    const[credentials, setCredentials] = useState({email:"", password:""})

    const onChange = (e) =>{
        setCredentials({...credentials,[e.target.name]:e.target.value})
    }
    const LogIn = async (e) =>{
        e.preventDefault();
        const response = await fetch("http://localhost:5000/api/auth/login", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email:credentials.email, password:credentials.password }) // body data type must match "Content-Type" header
        });
        const res = await response.json(); // parses JSON response into native JavaScript objects

        console.log("response",res.error)
        if(res.e)
        {
            localStorage.setItem("token",res)
            console.log("authtoken", res)
            navigate('/')
        }
        else{
            alert("Invalid Credentials")
        }
    }
    return (
        <div className="container my-3">
            <form>
                <div className="mb-3">
                    <label for="exampleInputEmail1" className="form-label">Email address</label>
                    <input type="email" className="form-control" id="exampleInputEmail1" name="email" value={credentials.email} onChange={onChange} aria-describedby="emailHelp"/>
                        <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div className="mb-3">
                    <label for="exampleInputPassword1" className="form-label">Password</label>
                    <input type="password" className="form-control" name="password" value={credentials.password} onChange={onChange} id="exampleInputPassword1"/>
                </div>
                
                <button type="submit" className="btn btn-danger" onClick={LogIn}>LogIn</button>
            </form>
        </div>
    )
}

export default Login
