import React, { useState } from "react";
import NoteContext from "./NoteContext";

const NoteState = (props) => {
    const host="http://localhost:5000"
    const initialNotes = [
        {
            "_id": "633d444a94e53d28acf69069",
            "user": "6332ad85e578c5f1edd077c8",
            "title": "my title",
            "description": "Please wake up early",
            "tag": "personal",
            "timeStamp": "2022-10-05T08:46:02.169Z",
            "__v": 0
        },
        {
            "_id": "63458d55f14069f42a9d1010",
            "user": "6332ad85e578c5f1edd077c8",
            "title": "your title",
            "description": "Please wake up early",
            "tag": "personal",
            "timeStamp": "2022-10-11T15:35:49.273Z",
            "__v": 0
        },
        {
            "_id": "63458d5df14069f42a9d1012",
            "user": "6332ad85e578c5f1edd077c8",
            "title": "Our title",
            "description": "Please wake up early",
            "tag": "personal",
            "timeStamp": "2022-10-11T15:35:57.637Z",
            "__v": 0
        },
        {
            "_id": "63458d63f14069f42a9d1014",
            "user": "6332ad85e578c5f1edd077c8",
            "title": "Sab ka title",
            "description": "Please wake up early",
            "tag": "personal",
            "timeStamp": "2022-10-11T15:36:03.865Z",
            "__v": 0
        }
    ]

    const [notes, noteState] = useState(initialNotes);

    // Add a note
    const addNote = async(title, description, tag) => {
        //Api
        const response = await fetch(`${host}/api/notes/savenotes`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'auth-token':"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjMzMmFkODVlNTc4YzVmMWVkZDA3N2M4In0sImlhdCI6MTY2NDMwMzA2NX0.tP7yGHswDPrZK_gmIvys56TbC_jP42CTsq7Ss4VKkiY"
            },
            body: JSON.stringify() // body data type must match "Content-Type" header
        });
        const json =  response.json(); // parses JSON response into native JavaScript objects
        //Logic
        const note = {
            "_id": "63458d63f14069f42a9d1014",
            "user": "6332ad85e578c5f1edd077c8",
            "title": title,
            "description": description,
            "tag": tag,
            "timeStamp": "2022-10-11T15:36:03.865Z",
            "__v": 0
        }
        noteState(notes.concat(note))
    }

    // delete a note
    const deleteNote = (id) => {

        const filterNote = notes.filter((notee) => {
            return notee._id !== id
        })

        noteState(filterNote);
    }

    //edit a note
    const editNote = async (id, title, description, tag) => {
        // Api call

        const response = await fetch(`${host}/api/notes/updatenot/${id}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'auth-token':"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjMzMmFkODVlNTc4YzVmMWVkZDA3N2M4In0sImlhdCI6MTY2NDMwMzA2NX0.tP7yGHswDPrZK_gmIvys56TbC_jP42CTsq7Ss4VKkiY"
            },
            body: JSON.stringify() // body data type must match "Content-Type" header
        });
        const json =  response.json(); // parses JSON response into native JavaScript objects

        //logic
        for (let indx = 0; indx < notes.length; indx++) {
            const element = notes[indx];
            if (element._id === id) {
                element.title = title;
                element.description = description;
                element.tag = tag;
            }
        }
    }

    return (


        <NoteContext.Provider value={{ notes, noteState, addNote, deleteNote, editNote }}>
            {props.children}

        </NoteContext.Provider>
    )
}

export default NoteState
